    public function watch_order_book ($symbol, $limit = null, $params = array ()) {
        $this->load_markets();
        $market = $this->market ($symbol);
        $name = 'level2';
        $messageHash = $name . ':' . $market['id'];
        $url = $this->urls['api']['ws'];
